list-view-config.query-select:
PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX obo: <http://purl.obolibrary.org/obo/>
select DISTINCT ?activity ?activityLabel ?activityDesc ?role $roleLabel (GROUP_CONCAT(?courseterm ; separator=" / ") as ?terms) where { ?subject ?property ?role . ?role obo:BFO_0000054 ?activity . ?activity rdfs:label ?activityLabel . ?role rdfs:label ?roleLabel . ?role core:dateTimeInterval ?dti . ?dti core:description ?courseterm . ?dti rdf:type core:DateTimeInterval . ?dti core:start ?dtidstart . ?dtidstart core:dateTime ?dstart . OPTIONAL { ?activity core:description ?activityDesc }
} GROUP by ?activity ?activityLabel ?activityDesc ?role $roleLabel order by ?activityLabel ?courseterm
